select * from abc_loan




	limit #{pageCondition.pageOffset,jdbcType=INTEGER},
	#{pageCondition.pageSize,jdbcType=INTEGER}

	select
        count(*)
        from abc_loan 



                and loan_id in
                (
                select loan_id
                from abc_loan a left join abc_charge_record b on a.loan_id =
                b.cr_loan_id
                where
                    b.cr_fee_type IN(2,3,NULL)
                    group by a.loan_id
                    HAVING 1=1

                        and IFNULL(SUM(b.cr_fee),0) >=#{minSer,jdbcType=DECIMAL}


                        and  IFNULL(SUM(b.cr_fee),0) <= #{maxSer,jdbcType=DECIMAL}

                ) 


                and loan_id in (
                select loan_id
                from abc_loan a left join abc_charge_record b on a.loan_id =
                b.cr_loan_id
                where
                    b.cr_fee_type IN (1,4,5,NULL)
                    group by a.loan_id
                    HAVING 1=1

                        and IFNULL(SUM(b.cr_fee),0) >=#{minCha,jdbcType=DECIMAL}


                        and  IFNULL(SUM(b.cr_fee),0) <= #{maxCha,jdbcType=DECIMAL}

                )

select

from abc_loan 



		and loan_id in
		(
		select loan_id
		from abc_loan a left join abc_charge_record b on a.loan_id =
		b.cr_loan_id
		where
			b.cr_fee_type IN(2,3,NULL)
			group by a.loan_id
	        HAVING 1=1

	            and IFNULL(SUM(b.cr_fee),0) >=#{minSer,jdbcType=DECIMAL}


	            and  IFNULL(SUM(b.cr_fee),0) <= #{maxSer,jdbcType=DECIMAL}

	    ) 


	    and loan_id in (
	    select loan_id
        from abc_loan a left join abc_charge_record b on a.loan_id =
        b.cr_loan_id
        where
            b.cr_fee_type IN (1,4,5,NULL)
            group by a.loan_id
            HAVING 1=1

                and IFNULL(SUM(b.cr_fee),0) >=#{minCha,jdbcType=DECIMAL}


                and  IFNULL(SUM(b.cr_fee),0) <= #{maxCha,jdbcType=DECIMAL}

        )



	limit #{pageCondition.pageOffset,jdbcType=INTEGER},
