select T1.loan_id as loanId,T1.loan_no as loanNo,T1.loan_money as loanMoney,T1.loan_period as loanPeriod,T1.loan_period_type as loanPeriodType,T2.Hasinterest as alreadyMoney,T3.Torepay as waitMoney,T3.Wperiodnumber as waitPeriod from abc_loan T1 left JOIN ( select a.pp_loan_id,a.pp_pay_state,sum(a.pp_collect_total) as Hasinterest from abc_plan_payment a group by a.pp_loan_id,a.pp_pay_state )T2 on T1.loan_id=T2.pp_loan_id and T2.pp_pay_state=2 left JOIN ( select a.pp_loan_id,a.pp_pay_state,sum(a.pp_pay_total_money) as Torepay,count(*) as Wperiodnumber from abc_plan_payment a group by a.pp_loan_id,a.pp_pay_state )T3 on T1.loan_id=T3.pp_loan_id and T3.pp_pay_state=0 WHERE 1=1 and T1.loan_state=15
and T1.loan_user_id = #{userId}
limit #{pageCondition.pageOffset,jdbcType=INTEGER}, #{pageCondition.pageSize,jdbcType=INTEGER}
