mapper.select:
	    SELECT account_user_id AS userId,account_user_type AS userType
        FROM abc_recharge_record JOIN abc_account_info ON account_no = recharge_account_id
        WHERE recharge_seq_no = #{seqNo}

SELECT a.user_name AS custName, a.user_real_name AS custFullName, c.recharge_account_id AS extcashNumberofBank, b.account_bank_name AS extcashBank, c.recharge_amount AS extcashMoney, c.recharge_date AS extcashDate, c.recharge_state AS extcashState FROM abc_user a INNER JOIN abc_account_info b ON a.user_id = b.account_user_id INNER JOIN abc_recharge_record c ON b.account_mark = c.recharge_account_id
1=1
and a.user_name like concat('%', #{tocashRecordJDO.custName}, '%')
and c.recharge_state = #{tocashRecordJDO.extcashState,jdbcType=INTEGER}
and c.recharge_date between
#{startDate,jdbcType=TIMESTAMP}
'1970-01-01'
and
#{endDate,jdbcType=TIMESTAMP}
now()
and b.account_user_type in ('1','2')
limit #{pageCondition.pageOffset,jdbcType=INTEGER}, #{pageCondition.pageSize,jdbcType=INTEGER}

SELECT a.emp_name AS custName, a.emp_real_name AS custFullName, c.recharge_account_id AS extcashNumberofBank, b.account_bank_name AS extcashBank, c.recharge_amount AS extcashMoney, c.recharge_date AS extcashDate, c.recharge_state AS extcashState FROM abc_employee a INNER JOIN abc_account_info b ON a.emp_id = b.account_user_id INNER JOIN abc_recharge_record c ON b.account_no = c.recharge_account_id
1=1
and a.emp_real_name like concat('%', #{tocashRecordJDO.custName}, '%')
and c.recharge_state = #{tocashRecordJDO.extcashState,jdbcType=INTEGER}
and c.recharge_date between
#{startDate,jdbcType=TIMESTAMP}
'1970-01-01'
and
#{endDate,jdbcType=TIMESTAMP}
now()
and b.account_user_type = '3'
GROUP BY a.emp_id
limit #{pageCondition.pageOffset,jdbcType=INTEGER}, #{pageCondition.pageSize,jdbcType=INTEGER}

